      var ad_medium_1 = this.getSlotTemplate(this.ads.medium_1);
      var ad_medium_2 = this.getSlotTemplate(this.ads.medium_2);
      var ad_medium_3 = this.getSlotTemplate(this.ads.medium_3);
      var ad_tablet_1 = this.getSlotTemplate(this.ads.tablet_1);
